import React from "react";
import {
    BodyContainer,
} from "../StyledComponents/StyledComponents";
import {
    FooterContainer,
    FooterLayout,
    FooterCopyright
} from '../Footer/StyledComponents';
import SocialButtons from "../Social/SocialButtons";
import Button from "../Utility/Button";
import { Fade } from "react-awesome-reveal";
import Data from '../../Data';

const Footer = (props) => (
    <Fade direction="top" delay={250} triggerOnce>
        {
            props.onCaseStudy

                ?

            <FooterContainer>
                <FooterLayout center>
                    <Button
                        left
                        route="/work"
                        text="Back to work"
                        color={props.color}
                    />
                </FooterLayout>                
            </FooterContainer> 

                :

            <FooterContainer>
                <BodyContainer>
                    <FooterLayout>
                        <FooterCopyright>
                            <p>
                                Â© {`${(new Date().getFullYear())} ${Data.home.name}`} 
                            </p>
                        </FooterCopyright>
                        <SocialButtons />
                    </FooterLayout>
                </BodyContainer>
            </FooterContainer>
        }
    </Fade>
);

export default Footer;
